Run overture on Docker and FreeBSD 12 ENV foo /overtureWORKDIR${foo}RUN apt-get update && apt-get install -y ca-certificatesRUN printf "deb https://mirrors.ustc.edu.cn/ubuntu/ bionic main restricted universe multiverse
 deb-src https://mirrors.ustc.edu.cn/ubuntu/ bionic main restricted universe multiverse
 deb https://mirrors.ustc.edu.cn/ubuntu/ bionic-security main restricted universe multiverse
 deb-src https://mirrors.ustc.edu.cn/ubuntu/ bionic-security main restricted universe multiverse
 deb https://mirrors.ustc.edu.cn/ubuntu/ bionic-updates main restricted universe multiverse
 deb-src https://mirrors.ustc.edu.cn/ubuntu/ bionic-updates main restricted universe multiverse
 deb https://mirrors.ustc.edu.cn/ubuntu/ bionic-backports main restricted universe multiverse
 deb-src https://mirrors.ustc.edu.cn/ubuntu/ bionic-backports main restricted universe multiverse" > /etc/apt/sources.listRUN apt-get updateRUN apt-get upgrade -yRUN apt-get install -y wget curl unzip apt-utils vimRUN cd /overtureRUN wget https://github.com/shawn1m/overture/releases/download/v1.5-rc1/overture-linux-amd64.zipRUN unzip overture-linux-amd64.zipRUN curl https://raw.githubusercontent.com/gfwlist/gfwlist/master/gfwlist.txt | base64 -d | sort -u | sed '/^$\|@@/d'| sed 's#!.\+##; s#|##g; s#@##g; s#http://##; s#https://##;' | sed '/\*/d; /apple\.com/d; /sina\.cn/d; /sina\.com\.cn/d; /baidu\.com/d; /qq\.com/d' | sed '/^[0-9]\+\.[0-9]\+\.[0-9]\+\.[0-9]\+$/d' | grep '^[0-9a-zA-Z\.-]\+$' | grep '\.' | sed 's#^\.\+##' | sort -u > /tmp/temp_gfwlist.txtRUN curl https://raw.githubusercontent.com/hq450/fancyss/master/rules/gfwlist.conf | sed 's/ipset=/\.//g; s//gfwlist//g; /^server/d' > /tmp/temp_koolshare.txtRUN cat /tmp/temp_gfwlist.txt /tmp/temp_koolshare.txt | sort -u > gfw_all_domain.txtRUN wget https://raw.githubusercontent.com/17mon/china_ip_list/master/china_ip_list.txtRUN printf '{ 
 "BindAddress": ":53",
 "PrimaryDNS": [
 {
 "Name": "DNSPod",
 "Address": "114.114.114.114:53",
 "Protocol": "udp",
 "SOCKS5Address": "",
 "Timeout": 6,
 "EDNSClientSubnet": {
 "Policy": "auto",
 "ExternalIP": "222.90.74.60",
 "NoCookie": true
 }
 }
 ],
 "AlternativeDNS": [
 {
 "Name": "OpenDNS",
 "Address": "208.67.222.222:443",
 "Protocol": "tcp",
 "SOCKS5Address": "",
 "Timeout": 6,
 "EDNSClientSubnet": {
 "Policy": "manual",
 "ExternalIP": "202.60.225.114",
 "NoCookie": true
 }
 }
 ],
 "OnlyPrimaryDNS": false,
 "IPv6UseAlternativeDNS": false,
 "IPNetworkFile": {
 "Primary": "./china_ip_list.txt",
 "Alternative": "./ip_network_alternative_sample"
 },
 "DomainFile": {
 "Primary": "./domain_primary_sample",
 "Alternative": "./gfw_all_domain.txt"
 },
 "HostsFile": "./hosts_sample",
 "MinimumTTL": 0,
 "CacheSize" : 0,
 "RejectQtype": [255]
 }' > config.jsonCMD ["./overture-linux-amd64"]#ENTRYPOINT ["./overture-linux-amd64"]

